import { useState } from "react";
import { Card, Button } from "react-bootstrap";
import { Link } from "react-router-dom";
import "./Join.scss";

function Join() {
  const [username, setUsername] = useState("");
  const [room, setRoom] = useState("");

  function checkInput(event: { preventDefault: () => void }) {
    if (!username || !room) {
      event.preventDefault();
    }
  }

  return (
    <>
      <div className="outer-container">
        <Card className="join-container">
          <h1 className="join-header">Start chatting</h1>
          <h5>Choose a room to chat anonymously with other users</h5>
          <form onSubmit={checkInput}>
            <label>Your username:</label>
            <input
              placeholder="Username"
              className="join-input"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />

            <label>Your room:</label>
            <input
              placeholder="Room"
              className="join-input"
              type="text"
              value={room}
              onChange={(e) => setRoom(e.target.value)}
            />

            <Link to={`/chat?name=${username}&room=${room}`}>
              <Button variant='primary' className="join-button" type="submit">
                Join
              </Button>
            </Link>
          </form>
        </Card>
      </div>
    </>
  );
}

export default Join;
